Ext.data.JsonP.android_transition_ChangeText({"tagname":"class","name":"android.transition.ChangeText","autodetected":{},"files":[{"filename":"ChangeText.js","href":"ChangeText.html#android-transition-ChangeText"}],"extends":"android.transition.Transition","hide":true,"members":[{"name":"CHANGE_BEHAVIOR_IN","tagname":"property","owner":"android.transition.ChangeText","id":"property-CHANGE_BEHAVIOR_IN","meta":{}},{"name":"CHANGE_BEHAVIOR_KEEP","tagname":"property","owner":"android.transition.ChangeText","id":"property-CHANGE_BEHAVIOR_KEEP","meta":{}},{"name":"CHANGE_BEHAVIOR_OUT","tagname":"property","owner":"android.transition.ChangeText","id":"property-CHANGE_BEHAVIOR_OUT","meta":{}},{"name":"CHANGE_BEHAVIOR_OUT_IN","tagname":"property","owner":"android.transition.ChangeText","id":"property-CHANGE_BEHAVIOR_OUT_IN","meta":{}},{"name":"MATCH_ID","tagname":"property","owner":"android.transition.Transition","id":"property-MATCH_ID","meta":{}},{"name":"MATCH_INSTANCE","tagname":"property","owner":"android.transition.Transition","id":"property-MATCH_INSTANCE","meta":{}},{"name":"MATCH_ITEM_ID","tagname":"property","owner":"android.transition.Transition","id":"property-MATCH_ITEM_ID","meta":{}},{"name":"MATCH_NAME","tagname":"property","owner":"android.transition.Transition","id":"property-MATCH_NAME","meta":{}},{"name":"addListener","tagname":"method","owner":"android.transition.Transition","id":"method-addListener","meta":{}},{"name":"addTarget","tagname":"method","owner":"android.transition.Transition","id":"method-addTarget","meta":{}},{"name":"canRemoveViews","tagname":"method","owner":"android.transition.Transition","id":"method-canRemoveViews","meta":{}},{"name":"captureEndValues","tagname":"method","owner":"android.transition.ChangeText","id":"method-captureEndValues","meta":{}},{"name":"captureStartValues","tagname":"method","owner":"android.transition.ChangeText","id":"method-captureStartValues","meta":{}},{"name":"clone","tagname":"method","owner":"android.transition.Transition","id":"method-clone","meta":{}},{"name":"createAnimator","tagname":"method","owner":"android.transition.ChangeText","id":"method-createAnimator","meta":{}},{"name":"excludeChildren","tagname":"method","owner":"android.transition.Transition","id":"method-excludeChildren","meta":{}},{"name":"excludeTarget","tagname":"method","owner":"android.transition.Transition","id":"method-excludeTarget","meta":{}},{"name":"getChangeBehavior","tagname":"method","owner":"android.transition.ChangeText","id":"method-getChangeBehavior","meta":{}},{"name":"getDuration","tagname":"method","owner":"android.transition.Transition","id":"method-getDuration","meta":{}},{"name":"getEpicenter","tagname":"method","owner":"android.transition.Transition","id":"method-getEpicenter","meta":{}},{"name":"getEpicenterCallback","tagname":"method","owner":"android.transition.Transition","id":"method-getEpicenterCallback","meta":{}},{"name":"getInterpolator","tagname":"method","owner":"android.transition.Transition","id":"method-getInterpolator","meta":{}},{"name":"getName","tagname":"method","owner":"android.transition.Transition","id":"method-getName","meta":{}},{"name":"getPathMotion","tagname":"method","owner":"android.transition.Transition","id":"method-getPathMotion","meta":{}},{"name":"getPropagation","tagname":"method","owner":"android.transition.Transition","id":"method-getPropagation","meta":{}},{"name":"getStartDelay","tagname":"method","owner":"android.transition.Transition","id":"method-getStartDelay","meta":{}},{"name":"getTargetIds","tagname":"method","owner":"android.transition.Transition","id":"method-getTargetIds","meta":{}},{"name":"getTargetNames","tagname":"method","owner":"android.transition.Transition","id":"method-getTargetNames","meta":{}},{"name":"getTargetTypes","tagname":"method","owner":"android.transition.Transition","id":"method-getTargetTypes","meta":{}},{"name":"getTargets","tagname":"method","owner":"android.transition.Transition","id":"method-getTargets","meta":{}},{"name":"getTransitionProperties","tagname":"method","owner":"android.transition.ChangeText","id":"method-getTransitionProperties","meta":{}},{"name":"getTransitionValues","tagname":"method","owner":"android.transition.Transition","id":"method-getTransitionValues","meta":{}},{"name":"isTransitionRequired","tagname":"method","owner":"android.transition.Transition","id":"method-isTransitionRequired","meta":{}},{"name":"removeListener","tagname":"method","owner":"android.transition.Transition","id":"method-removeListener","meta":{}},{"name":"removeTarget","tagname":"method","owner":"android.transition.Transition","id":"method-removeTarget","meta":{}},{"name":"setChangeBehavior","tagname":"method","owner":"android.transition.ChangeText","id":"method-setChangeBehavior","meta":{}},{"name":"setDuration","tagname":"method","owner":"android.transition.Transition","id":"method-setDuration","meta":{}},{"name":"setEpicenterCallback","tagname":"method","owner":"android.transition.Transition","id":"method-setEpicenterCallback","meta":{}},{"name":"setInterpolator","tagname":"method","owner":"android.transition.Transition","id":"method-setInterpolator","meta":{}},{"name":"setMatchOrder","tagname":"method","owner":"android.transition.Transition","id":"method-setMatchOrder","meta":{}},{"name":"setPathMotion","tagname":"method","owner":"android.transition.Transition","id":"method-setPathMotion","meta":{}},{"name":"setPropagation","tagname":"method","owner":"android.transition.Transition","id":"method-setPropagation","meta":{}},{"name":"setStartDelay","tagname":"method","owner":"android.transition.Transition","id":"method-setStartDelay","meta":{}},{"name":"toString","tagname":"method","owner":"android.transition.Transition","id":"method-toString","meta":{}}],"alternateClassNames":[],"aliases":{},"id":"class-android.transition.ChangeText","short_doc":"This transition tracks changes to the text in TextView targets. ...","classIcon":"icon-class","superclasses":["java.lang.Object","android.transition.Transition"],"subclasses":[],"mixedInto":[],"mixins":[],"parentMixins":[],"requires":[],"uses":[],"html":"<div><pre class=\"hierarchy\"><h4>Hierarchy</h4><div class='subclass first-child'>java.lang.Object<div class='subclass '><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='docClass'>android.transition.Transition</a><div class='subclass '><strong>android.transition.ChangeText</strong></div></div></div><h4>Files</h4><div class='dependency'><a href='source/ChangeText.html#android-transition-ChangeText' target='_blank'>ChangeText.js</a></div></pre><div class='doc-contents'><p>This transition tracks changes to the text in TextView targets. If the text\n changes between the start and end scenes, the transition ensures that the\n starting text stays until the transition ends, at which point it changes\n to the end text.  This is useful in situations where you want to resize a\n text view to its new size before displaying the text that goes there.</p>\n</div><div class='members'><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-property'>Properties</h3><div class='subsection'><div id='property-CHANGE_BEHAVIOR_IN' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-property-CHANGE_BEHAVIOR_IN' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN' class='name expandable'>CHANGE_BEHAVIOR_IN</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Flag specifying that the text changing animation should fade in the\nend text into the affected target view(s). ...</div><div class='long'><p>Flag specifying that the text changing animation should fade in the\nend text into the affected target view(s). This transition is typically\nused in conjunction with an earlier <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" class=\"docClass\">CHANGE_BEHAVIOR_OUT</a>\ntransition, possibly with other transitions running as well, such as\na sequence to fade out, then resize the view, then fade in.</p>\n\n<p>@see <a href=\"#!/api/android.transition.ChangeText-method-setChangeBehavior\" rel=\"android.transition.ChangeText-method-setChangeBehavior\" class=\"docClass\">setChangeBehavior</a>(int)</p>\n<p>Defaults to: <code>&quot;2&quot;</code></p></div></div></div><div id='property-CHANGE_BEHAVIOR_KEEP' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-property-CHANGE_BEHAVIOR_KEEP' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_KEEP' class='name expandable'>CHANGE_BEHAVIOR_KEEP</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Flag specifying that the text in affected/changing TextView targets will keep\ntheir original text during the transiti...</div><div class='long'><p>Flag specifying that the text in affected/changing TextView targets will keep\ntheir original text during the transition, setting it to the final text when\nthe transition ends. This is the default behavior.</p>\n\n<p>@see <a href=\"#!/api/android.transition.ChangeText-method-setChangeBehavior\" rel=\"android.transition.ChangeText-method-setChangeBehavior\" class=\"docClass\">setChangeBehavior</a>(int)</p>\n<p>Defaults to: <code>&quot;0&quot;</code></p></div></div></div><div id='property-CHANGE_BEHAVIOR_OUT' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-property-CHANGE_BEHAVIOR_OUT' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT' class='name expandable'>CHANGE_BEHAVIOR_OUT</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Flag specifying that the text changing animation should first fade\nout the original text completely. ...</div><div class='long'><p>Flag specifying that the text changing animation should first fade\nout the original text completely. The new text is set on the target\nview at the end of the fade-out animation. This transition is typically\nused with a later <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" class=\"docClass\">CHANGE_BEHAVIOR_IN</a> transition, allowing more\nflexibility than the <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" class=\"docClass\">CHANGE_BEHAVIOR_OUT_IN</a> by allowing other\ntransitions to be run sequentially or in parallel with these fades.</p>\n\n<p>@see <a href=\"#!/api/android.transition.ChangeText-method-setChangeBehavior\" rel=\"android.transition.ChangeText-method-setChangeBehavior\" class=\"docClass\">setChangeBehavior</a>(int)</p>\n<p>Defaults to: <code>&quot;1&quot;</code></p></div></div></div><div id='property-CHANGE_BEHAVIOR_OUT_IN' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-property-CHANGE_BEHAVIOR_OUT_IN' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN' class='name expandable'>CHANGE_BEHAVIOR_OUT_IN</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Flag specifying that the text changing animation should first fade\nout the original text completely and then fade in ...</div><div class='long'><p>Flag specifying that the text changing animation should first fade\nout the original text completely and then fade in the\nnew text.</p>\n\n<p>@see <a href=\"#!/api/android.transition.ChangeText-method-setChangeBehavior\" rel=\"android.transition.ChangeText-method-setChangeBehavior\" class=\"docClass\">setChangeBehavior</a>(int)</p>\n<p>Defaults to: <code>&quot;3&quot;</code></p></div></div></div><div id='property-MATCH_ID' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-property-MATCH_ID' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-property-MATCH_ID' class='name expandable'>MATCH_ID</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>With setMatchOrder(int...), chooses to match by\nandroid.view.View.getId(). ...</div><div class='long'><p>With setMatchOrder(int...), chooses to match by\nandroid.view.View.getId(). Negative IDs will not be matched.</p>\n<p>Defaults to: <code>&quot;3&quot;</code></p></div></div></div><div id='property-MATCH_INSTANCE' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-property-MATCH_INSTANCE' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-property-MATCH_INSTANCE' class='name expandable'>MATCH_INSTANCE</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>With setMatchOrder(int...), chooses to match by View instance. ...</div><div class='long'><p>With setMatchOrder(int...), chooses to match by View instance.</p>\n<p>Defaults to: <code>&quot;1&quot;</code></p></div></div></div><div id='property-MATCH_ITEM_ID' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-property-MATCH_ITEM_ID' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-property-MATCH_ITEM_ID' class='name expandable'>MATCH_ITEM_ID</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>With setMatchOrder(int...), chooses to match by the android.widget.Adapter\nitem id. ...</div><div class='long'><p>With setMatchOrder(int...), chooses to match by the android.widget.Adapter\nitem id. When android.widget.Adapter.hasStableIds() returns false, no match\nwill be made for items.</p>\n<p>Defaults to: <code>&quot;4&quot;</code></p></div></div></div><div id='property-MATCH_NAME' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-property-MATCH_NAME' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-property-MATCH_NAME' class='name expandable'>MATCH_NAME</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>With setMatchOrder(int...), chooses to match by\nandroid.view.View.getTransitionName(). ...</div><div class='long'><p>With setMatchOrder(int...), chooses to match by\nandroid.view.View.getTransitionName(). Null names will not be matched.</p>\n<p>Defaults to: <code>&quot;2&quot;</code></p></div></div></div></div></div><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-method'>Methods</h3><div class='subsection'><div id='method-addListener' class='member first-child inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-addListener' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-addListener' class='name expandable'>addListener</a>( <span class='pre'>listener</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Adds a listener to the set of listeners that are sent events through the\n life of an animation, such as start, repeat...</div><div class='long'><p>Adds a listener to the set of listeners that are sent events through the\n life of an animation, such as start, repeat, and end.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>listener</span> : Object {Transition.TransitionListener}<div class='sub-desc'><p>the listener to be added to the current set of listeners\n for this animation.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.</p>\n</div></li></ul></div></div></div><div id='method-addTarget' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-addTarget' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-addTarget' class='name expandable'>addTarget</a>( <span class='pre'>target, target</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the target view instances that this Transition is interested in\n animating. ...</div><div class='long'><p>Sets the target view instances that this Transition is interested in\n animating. By default, there are no targets, and a Transition will\n listen for changes on every view in the hierarchy below the sceneRoot\n of the Scene being transitioned into. Setting targets constrains\n the Transition to only listen for, and act on, these views.\n All other views will be ignored.</p>\n\n<p> <p>The target list is like the targetId\n list except this list specifies the actual View instances, not the ids\n of the views. This is an important distinction when scene changes involve\n view hierarchies which have been inflated separately; different views may\n share the same id but not actually be the same instance. If the transition\n should treat those views as the same, then <a href=\"#!/api/android.transition.Transition-method-addTarget\" rel=\"android.transition.Transition-method-addTarget\" class=\"docClass\">addTarget</a>(int) should be used\n instead of <a href=\"#!/api/android.transition.Transition-method-addTarget\" rel=\"android.transition.Transition-method-addTarget\" class=\"docClass\">addTarget</a>(View). If, on the other hand, scene changes involve\n changes all within the same view hierarchy, among views which do not\n necessarily have ids set on them, then the target list of views may be more\n convenient.</p></p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>target</span> : Object {View}<div class='sub-desc'><p>A View on which the Transition will act, must be non-null.</p>\n</div></li><li><span class='pre'>target</span> : Object<div class='sub-desc'><p>A View on which the Transition will act, must be non-null.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>The Transition to which the target is added.\n Returning the same object makes it easier to chain calls during\n construction, such as\n <code>transitionSet.addTransitions(new Fade()).addTarget(someView);</code></p>\n</div></li></ul></div></div></div><div id='method-canRemoveViews' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-canRemoveViews' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-canRemoveViews' class='name expandable'>canRemoveViews</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n</div></div></div><div id='method-captureEndValues' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-captureEndValues' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-captureEndValues' class='name expandable'>captureEndValues</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n<p>Overrides: <a href=\"#!/api/android.transition.Transition-method-captureEndValues\" rel=\"android.transition.Transition-method-captureEndValues\" class=\"docClass\">android.transition.Transition.captureEndValues</a></p></div></div></div><div id='method-captureStartValues' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-captureStartValues' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-captureStartValues' class='name expandable'>captureStartValues</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n<p>Overrides: <a href=\"#!/api/android.transition.Transition-method-captureStartValues\" rel=\"android.transition.Transition-method-captureStartValues\" class=\"docClass\">android.transition.Transition.captureStartValues</a></p></div></div></div><div id='method-clone' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-clone' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-clone' class='name expandable'>clone</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n</div></div></div><div id='method-createAnimator' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-createAnimator' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-createAnimator' class='name expandable'>createAnimator</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n<p>Overrides: <a href=\"#!/api/android.transition.Transition-method-createAnimator\" rel=\"android.transition.Transition-method-createAnimator\" class=\"docClass\">android.transition.Transition.createAnimator</a></p></div></div></div><div id='method-excludeChildren' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-excludeChildren' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-excludeChildren' class='name expandable'>excludeChildren</a>( <span class='pre'>type, exclude, type, exclude</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Whether to add the given type to the list of types whose children should\n be excluded from this transition. ...</div><div class='long'><p>Whether to add the given type to the list of types whose children should\n be excluded from this transition. The <code>exclude</code> parameter\n specifies whether the target type should be added to or removed from\n the excluded list.</p>\n\n<p> <p>Excluding targets is a general mechanism for allowing transitions to run on\n a view hierarchy while skipping target views that should not be part of\n the transition. For example, you may want to avoid animating children\n of a specific ListView or Spinner. Views can be excluded either by their\n id, or by their instance reference, or by the Class of that view\n (eg, Spinner).</p></p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>type</span> : Object {java.lang.Class}<div class='sub-desc'><p>The type to ignore when running this transition.</p>\n</div></li><li><span class='pre'>exclude</span> : Boolean<div class='sub-desc'><p>Whether to add the target type to or remove it from the\n current list of excluded target types.\n@see <a href=\"#!/api/android.transition.Transition-method-excludeChildren\" rel=\"android.transition.Transition-method-excludeChildren\" class=\"docClass\">excludeChildren</a>(View, boolean)</p>\n</div></li><li><span class='pre'>type</span> : Object<div class='sub-desc'><p>The type to ignore when running this transition.</p>\n</div></li><li><span class='pre'>exclude</span> : Object<div class='sub-desc'><p>Whether to add the target type to or remove it from the\n current list of excluded target types.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.</p>\n</div></li></ul></div></div></div><div id='method-excludeTarget' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-excludeTarget' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-excludeTarget' class='name expandable'>excludeTarget</a>( <span class='pre'>type, exclude, type, exclude</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Whether to add the given type to the list of types to exclude from this\n transition. ...</div><div class='long'><p>Whether to add the given type to the list of types to exclude from this\n transition. The <code>exclude</code> parameter specifies whether the target\n type should be added to or removed from the excluded list.</p>\n\n<p> <p>Excluding targets is a general mechanism for allowing transitions to run on\n a view hierarchy while skipping target views that should not be part of\n the transition. For example, you may want to avoid animating children\n of a specific ListView or Spinner. Views can be excluded either by their\n id, or by their instance reference, or by the Class of that view\n (eg, Spinner).</p></p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>type</span> : Object {java.lang.Class}<div class='sub-desc'><p>The type to ignore when running this transition.</p>\n</div></li><li><span class='pre'>exclude</span> : Boolean<div class='sub-desc'><p>Whether to add the target type to or remove it from the\n current list of excluded target types.\n@see <a href=\"#!/api/android.transition.Transition-method-excludeTarget\" rel=\"android.transition.Transition-method-excludeTarget\" class=\"docClass\">excludeTarget</a>(View, boolean)</p>\n</div></li><li><span class='pre'>type</span> : Object<div class='sub-desc'><p>The type to ignore when running this transition.</p>\n</div></li><li><span class='pre'>exclude</span> : Object<div class='sub-desc'><p>Whether to add the target type to or remove it from the\n current list of excluded target types.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.</p>\n</div></li></ul></div></div></div><div id='method-getChangeBehavior' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-getChangeBehavior' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-getChangeBehavior' class='name expandable'>getChangeBehavior</a>( <span class='pre'></span> ) : Number<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the type of changing animation that will be run. ...</div><div class='long'><p>Returns the type of changing animation that will be run.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Number</span><div class='sub-desc'><p>either <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_KEEP\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_KEEP\" class=\"docClass\">CHANGE_BEHAVIOR_KEEP</a>, <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" class=\"docClass\">CHANGE_BEHAVIOR_OUT</a>,\n <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" class=\"docClass\">CHANGE_BEHAVIOR_IN</a>, or <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" class=\"docClass\">CHANGE_BEHAVIOR_OUT_IN</a>.</p>\n</div></li></ul></div></div></div><div id='method-getDuration' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getDuration' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getDuration' class='name expandable'>getDuration</a>( <span class='pre'></span> ) : Number<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the duration set on this transition. ...</div><div class='long'><p>Returns the duration set on this transition. If no duration has been set,\n the returned value will be negative, indicating that resulting animators will\n retain their own durations.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Number</span><div class='sub-desc'><p>The duration set on this transition, in milliseconds, if one has been\n set, otherwise returns a negative number.</p>\n</div></li></ul></div></div></div><div id='method-getEpicenter' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getEpicenter' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getEpicenter' class='name expandable'>getEpicenter</a>( <span class='pre'></span> ) : Object {android.graphics.Rect}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the epicenter as specified by the\n android.transition.Transition.EpicenterCallback or null if no callback exi...</div><div class='long'><p>Returns the epicenter as specified by the\n <a href=\"#!/api/android.transition.Transition.EpicenterCallback\" rel=\"android.transition.Transition.EpicenterCallback\" class=\"docClass\">android.transition.Transition.EpicenterCallback</a> or null if no callback exists.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.graphics.Rect}</span><div class='sub-desc'><p>the epicenter as specified by the\n <a href=\"#!/api/android.transition.Transition.EpicenterCallback\" rel=\"android.transition.Transition.EpicenterCallback\" class=\"docClass\">android.transition.Transition.EpicenterCallback</a> or null if no callback exists.\n@see <a href=\"#!/api/android.transition.Transition-method-setEpicenterCallback\" rel=\"android.transition.Transition-method-setEpicenterCallback\" class=\"docClass\">setEpicenterCallback</a>(<a href=\"#!/api/android.transition.Transition.EpicenterCallback\" rel=\"android.transition.Transition.EpicenterCallback\" class=\"docClass\">android.transition.Transition.EpicenterCallback</a>)</p>\n</div></li></ul></div></div></div><div id='method-getEpicenterCallback' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getEpicenterCallback' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getEpicenterCallback' class='name expandable'>getEpicenterCallback</a>( <span class='pre'></span> ) : Object {android.transition.Transition.EpicenterCallback}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the callback used to find the epicenter of the Transition. ...</div><div class='long'><p>Returns the callback used to find the epicenter of the Transition.\n Transitions like <a href=\"#!/api/android.transition.Explode\" rel=\"android.transition.Explode\" class=\"docClass\">android.transition.Explode</a> use a point or Rect to orient\n the direction of travel. This is called the epicenter of the Transition and is\n typically centered on a touched View. The\n <a href=\"#!/api/android.transition.Transition.EpicenterCallback\" rel=\"android.transition.Transition.EpicenterCallback\" class=\"docClass\">android.transition.Transition.EpicenterCallback</a> allows a Transition to\n dynamically retrieve the epicenter during a Transition.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition.EpicenterCallback}</span><div class='sub-desc'><p>the callback used to find the epicenter of the Transition.</p>\n</div></li></ul></div></div></div><div id='method-getInterpolator' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getInterpolator' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getInterpolator' class='name expandable'>getInterpolator</a>( <span class='pre'></span> ) : Object {android.animation.TimeInterpolator}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the interpolator set on this transition. ...</div><div class='long'><p>Returns the interpolator set on this transition. If no interpolator has been set,\n the returned value will be null, indicating that resulting animators will\n retain their own interpolators.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.animation.TimeInterpolator}</span><div class='sub-desc'><p>The interpolator set on this transition, if one has been set, otherwise\n returns null.</p>\n</div></li></ul></div></div></div><div id='method-getName' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getName' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getName' class='name expandable'>getName</a>( <span class='pre'></span> ) : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the name of this Transition. ...</div><div class='long'><p>Returns the name of this Transition. This name is used internally to distinguish\n between different transitions to determine when interrupting transitions overlap.\n For example, a ChangeBounds running on the same target view as another ChangeBounds\n should determine whether the old transition is animating to different end values\n and should be canceled in favor of the new transition.</p>\n\n<p> <p>By default, a Transition's name is simply the value of Class.getName(),\n but subclasses are free to override and return something different.</p></p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>String</span><div class='sub-desc'><p>The name of this transition.</p>\n</div></li></ul></div></div></div><div id='method-getPathMotion' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getPathMotion' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getPathMotion' class='name expandable'>getPathMotion</a>( <span class='pre'></span> ) : Object {android.transition.PathMotion}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the algorithm object used to interpolate along two dimensions. ...</div><div class='long'><p>Returns the algorithm object used to interpolate along two dimensions. This is typically\n used to determine the View motion between two points.</p>\n\n<p> <p>\n     When describing in XML, use a nested XML tag for the path motion. It can be one of\n     the built-in tags <code>arcMotion</code> or <code>patternPathMotion</code> or it can\n     be a custom PathMotion using <code>pathMotion</code> with the <code>class</code>\n     attributed with the fully-described class name. For example:</p>\n <pre>{@code\n <changeBounds>\n     <pathMotion class=\"my.app.transition.MyPathMotion\"/>\n </pathMotion></changeBounds>}\n </pre>\n <p>or</p>\n <pre>{@code\n <changeBounds>\n   <arcMotion android:minimumHorizontalAngle=\"15\"\n              android:minimumVerticalAngle=\"0\"\n              android:maximumAngle=\"90\"/>\n </arcMotion></changeBounds>}\n </pre></p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.PathMotion}</span><div class='sub-desc'><p>The algorithm object used to interpolate along two dimensions.\n@see <a href=\"#!/api/android.transition.ArcMotion\" rel=\"android.transition.ArcMotion\" class=\"docClass\">android.transition.ArcMotion</a>\n@see PatternPathMotion\n@see <a href=\"#!/api/android.transition.PathMotion\" rel=\"android.transition.PathMotion\" class=\"docClass\">android.transition.PathMotion</a></p>\n</div></li></ul></div></div></div><div id='method-getPropagation' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getPropagation' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getPropagation' class='name expandable'>getPropagation</a>( <span class='pre'></span> ) : Object {android.transition.TransitionPropagation}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the android.transition.TransitionPropagation used to calculate Animator start\n delays. ...</div><div class='long'><p>Returns the <a href=\"#!/api/android.transition.TransitionPropagation\" rel=\"android.transition.TransitionPropagation\" class=\"docClass\">android.transition.TransitionPropagation</a> used to calculate Animator start\n delays.\n When a Transition affects several Views like <a href=\"#!/api/android.transition.Explode\" rel=\"android.transition.Explode\" class=\"docClass\">android.transition.Explode</a> or\n <a href=\"#!/api/android.transition.Slide\" rel=\"android.transition.Slide\" class=\"docClass\">android.transition.Slide</a>, there may be a desire to have a \"wave-front\" effect\n such that the Animator start delay depends on position of the View. The\n TransitionPropagation specifies how the start delays are calculated.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.TransitionPropagation}</span><div class='sub-desc'><p>the <a href=\"#!/api/android.transition.TransitionPropagation\" rel=\"android.transition.TransitionPropagation\" class=\"docClass\">android.transition.TransitionPropagation</a> used to calculate Animator start\n delays. This is null by default.</p>\n</div></li></ul></div></div></div><div id='method-getStartDelay' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getStartDelay' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getStartDelay' class='name expandable'>getStartDelay</a>( <span class='pre'></span> ) : Number<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the startDelay set on this transition. ...</div><div class='long'><p>Returns the startDelay set on this transition. If no startDelay has been set,\n the returned value will be negative, indicating that resulting animators will\n retain their own startDelays.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Number</span><div class='sub-desc'><p>The startDelay set on this transition, in milliseconds, if one has\n been set, otherwise returns a negative number.</p>\n</div></li></ul></div></div></div><div id='method-getTargetIds' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getTargetIds' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getTargetIds' class='name expandable'>getTargetIds</a>( <span class='pre'></span> ) : Object {java.util.List}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the list of target IDs that this transition limits itself to\n tracking and animating. ...</div><div class='long'><p>Returns the list of target IDs that this transition limits itself to\n tracking and animating. If the list is null or empty for\n <a href=\"#!/api/android.transition.Transition-method-getTargetIds\" rel=\"android.transition.Transition-method-getTargetIds\" class=\"docClass\">getTargetIds</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargets\" rel=\"android.transition.Transition-method-getTargets\" class=\"docClass\">getTargets</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargetNames\" rel=\"android.transition.Transition-method-getTargetNames\" class=\"docClass\">getTargetNames</a>(), and\n <a href=\"#!/api/android.transition.Transition-method-getTargetTypes\" rel=\"android.transition.Transition-method-getTargetTypes\" class=\"docClass\">getTargetTypes</a>() then this transition is\n not limited to specific views, and will handle changes to any views\n in the hierarchy of a scene change.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {java.util.List}</span><div class='sub-desc'><p>the list of target IDs</p>\n</div></li></ul></div></div></div><div id='method-getTargetNames' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getTargetNames' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getTargetNames' class='name expandable'>getTargetNames</a>( <span class='pre'></span> ) : Object {java.util.List}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the list of target transitionNames that this transition limits itself to\n tracking and animating. ...</div><div class='long'><p>Returns the list of target transitionNames that this transition limits itself to\n tracking and animating. If the list is null or empty for\n <a href=\"#!/api/android.transition.Transition-method-getTargetIds\" rel=\"android.transition.Transition-method-getTargetIds\" class=\"docClass\">getTargetIds</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargets\" rel=\"android.transition.Transition-method-getTargets\" class=\"docClass\">getTargets</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargetNames\" rel=\"android.transition.Transition-method-getTargetNames\" class=\"docClass\">getTargetNames</a>(), and\n <a href=\"#!/api/android.transition.Transition-method-getTargetTypes\" rel=\"android.transition.Transition-method-getTargetTypes\" class=\"docClass\">getTargetTypes</a>() then this transition is\n not limited to specific views, and will handle changes to any views\n in the hierarchy of a scene change.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {java.util.List}</span><div class='sub-desc'><p>the list of target transitionNames</p>\n</div></li></ul></div></div></div><div id='method-getTargetTypes' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getTargetTypes' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getTargetTypes' class='name expandable'>getTargetTypes</a>( <span class='pre'></span> ) : Object {java.util.List}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the list of target transitionNames that this transition limits itself to\n tracking and animating. ...</div><div class='long'><p>Returns the list of target transitionNames that this transition limits itself to\n tracking and animating. If the list is null or empty for\n <a href=\"#!/api/android.transition.Transition-method-getTargetIds\" rel=\"android.transition.Transition-method-getTargetIds\" class=\"docClass\">getTargetIds</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargets\" rel=\"android.transition.Transition-method-getTargets\" class=\"docClass\">getTargets</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargetNames\" rel=\"android.transition.Transition-method-getTargetNames\" class=\"docClass\">getTargetNames</a>(), and\n <a href=\"#!/api/android.transition.Transition-method-getTargetTypes\" rel=\"android.transition.Transition-method-getTargetTypes\" class=\"docClass\">getTargetTypes</a>() then this transition is\n not limited to specific views, and will handle changes to any views\n in the hierarchy of a scene change.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {java.util.List}</span><div class='sub-desc'><p>the list of target Types</p>\n</div></li></ul></div></div></div><div id='method-getTargets' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getTargets' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getTargets' class='name expandable'>getTargets</a>( <span class='pre'></span> ) : Object {java.util.List}<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns the list of target views that this transition limits itself to\n tracking and animating. ...</div><div class='long'><p>Returns the list of target views that this transition limits itself to\n tracking and animating. If the list is null or empty for\n <a href=\"#!/api/android.transition.Transition-method-getTargetIds\" rel=\"android.transition.Transition-method-getTargetIds\" class=\"docClass\">getTargetIds</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargets\" rel=\"android.transition.Transition-method-getTargets\" class=\"docClass\">getTargets</a>(), <a href=\"#!/api/android.transition.Transition-method-getTargetNames\" rel=\"android.transition.Transition-method-getTargetNames\" class=\"docClass\">getTargetNames</a>(), and\n <a href=\"#!/api/android.transition.Transition-method-getTargetTypes\" rel=\"android.transition.Transition-method-getTargetTypes\" class=\"docClass\">getTargetTypes</a>() then this transition is\n not limited to specific views, and will handle changes to any views\n in the hierarchy of a scene change.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {java.util.List}</span><div class='sub-desc'><p>the list of target views</p>\n</div></li></ul></div></div></div><div id='method-getTransitionProperties' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-getTransitionProperties' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-getTransitionProperties' class='name expandable'>getTransitionProperties</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n<p>Overrides: <a href=\"#!/api/android.transition.Transition-method-getTransitionProperties\" rel=\"android.transition.Transition-method-getTransitionProperties\" class=\"docClass\">android.transition.Transition.getTransitionProperties</a></p></div></div></div><div id='method-getTransitionValues' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-getTransitionValues' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-getTransitionValues' class='name expandable'>getTransitionValues</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>This method can be called by transitions to get the TransitionValues for\n any particular view during the transition-p...</div><div class='long'><p>This method can be called by transitions to get the TransitionValues for\n any particular view during the transition-playing process. This might be\n necessary, for example, to query the before/after state of related views\n for a given transition.</p>\n</div></div></div><div id='method-isTransitionRequired' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-isTransitionRequired' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-isTransitionRequired' class='name expandable'>isTransitionRequired</a>( <span class='pre'>startValues, endValues</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Returns whether or not the transition should create an Animator, based on the values\n captured during captureStartVal...</div><div class='long'><p>Returns whether or not the transition should create an Animator, based on the values\n captured during <a href=\"#!/api/android.transition.Transition-method-captureStartValues\" rel=\"android.transition.Transition-method-captureStartValues\" class=\"docClass\">captureStartValues</a>(TransitionValues) and\n <a href=\"#!/api/android.transition.Transition-method-captureEndValues\" rel=\"android.transition.Transition-method-captureEndValues\" class=\"docClass\">captureEndValues</a>(TransitionValues). The default implementation compares the\n property values returned from <a href=\"#!/api/android.transition.Transition-method-getTransitionProperties\" rel=\"android.transition.Transition-method-getTransitionProperties\" class=\"docClass\">getTransitionProperties</a>(), or all property values if\n {@code getTransitionProperties()} returns null. Subclasses may override this method to\n provide logic more specific to the transition implementation.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>startValues</span> : Object {TransitionValues}<div class='sub-desc'><p>the values from captureStartValues, This may be {@code null} if the\n                    View did not exist in the start state.</p>\n</div></li><li><span class='pre'>endValues</span> : Object {TransitionValues}<div class='sub-desc'><p>the values from captureEndValues. This may be {@code null} if the View\n                  did not exist in the end state.</p>\n</div></li></ul></div></div></div><div id='method-removeListener' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-removeListener' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-removeListener' class='name expandable'>removeListener</a>( <span class='pre'>listener</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Removes a listener from the set listening to this animation. ...</div><div class='long'><p>Removes a listener from the set listening to this animation.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>listener</span> : Object {Transition.TransitionListener}<div class='sub-desc'><p>the listener to be removed from the current set of\n listeners for this transition.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.</p>\n</div></li></ul></div></div></div><div id='method-removeTarget' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-removeTarget' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-removeTarget' class='name expandable'>removeTarget</a>( <span class='pre'>target</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Removes the given target from the list of targets that this Transition\n is interested in animating. ...</div><div class='long'><p>Removes the given target from the list of targets that this Transition\n is interested in animating.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>target</span> : Object {java.lang.Class}<div class='sub-desc'><p>The type of the target view, must be non-null.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>Transition The Transition from which the target is removed.\n Returning the same object makes it easier to chain calls during\n construction, such as\n <code>transitionSet.addTransitions(new Fade()).removeTarget(someType);</code></p>\n</div></li></ul></div></div></div><div id='method-setChangeBehavior' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='android.transition.ChangeText'>android.transition.ChangeText</span><br/><a href='source/ChangeText.html#android-transition-ChangeText-method-setChangeBehavior' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.ChangeText-method-setChangeBehavior' class='name expandable'>setChangeBehavior</a>( <span class='pre'>changeBehavior</span> ) : Object {android.transition.ChangeText}<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the type of changing animation that will be run, one of\n CHANGE_BEHAVIOR_KEEP, CHANGE_BEHAVIOR_OUT,\n CHANGE_BEHA...</div><div class='long'><p>Sets the type of changing animation that will be run, one of\n <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_KEEP\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_KEEP\" class=\"docClass\">CHANGE_BEHAVIOR_KEEP</a>, <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT\" class=\"docClass\">CHANGE_BEHAVIOR_OUT</a>,\n <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_IN\" class=\"docClass\">CHANGE_BEHAVIOR_IN</a>, and <a href=\"#!/api/android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" rel=\"android.transition.ChangeText-property-CHANGE_BEHAVIOR_OUT_IN\" class=\"docClass\">CHANGE_BEHAVIOR_OUT_IN</a>.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>changeBehavior</span> : Number<div class='sub-desc'><p>The type of fading animation to use when this\n transition is run.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.ChangeText}</span><div class='sub-desc'><p>this textChange object.</p>\n</div></li></ul></div></div></div><div id='method-setDuration' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setDuration' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setDuration' class='name expandable'>setDuration</a>( <span class='pre'>duration</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the duration of this transition. ...</div><div class='long'><p>Sets the duration of this transition. By default, there is no duration\n (indicated by a negative number), which means that the Animator created by\n the transition will have its own specified duration. If the duration of a\n Transition is set, that duration will override the Animator duration.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>duration</span> : Number<div class='sub-desc'><p>The length of the animation, in milliseconds.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.\n@attr ref android.R.styleable#Transition_duration</p>\n</div></li></ul></div></div></div><div id='method-setEpicenterCallback' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setEpicenterCallback' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setEpicenterCallback' class='name expandable'>setEpicenterCallback</a>( <span class='pre'>epicenterCallback</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the callback to use to find the epicenter of a Transition. ...</div><div class='long'><p>Sets the callback to use to find the epicenter of a Transition. A null value indicates\n that there is no epicenter in the Transition and onGetEpicenter() will return null.\n Transitions like <a href=\"#!/api/android.transition.Explode\" rel=\"android.transition.Explode\" class=\"docClass\">android.transition.Explode</a> use a point or Rect to orient\n the direction of travel. This is called the epicenter of the Transition and is\n typically centered on a touched View. The\n <a href=\"#!/api/android.transition.Transition.EpicenterCallback\" rel=\"android.transition.Transition.EpicenterCallback\" class=\"docClass\">android.transition.Transition.EpicenterCallback</a> allows a Transition to\n dynamically retrieve the epicenter during a Transition.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>epicenterCallback</span> : Object {Transition.EpicenterCallback}<div class='sub-desc'><p>The callback to use to find the epicenter of the Transition.</p>\n</div></li></ul></div></div></div><div id='method-setInterpolator' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setInterpolator' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setInterpolator' class='name expandable'>setInterpolator</a>( <span class='pre'>interpolator</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the interpolator of this transition. ...</div><div class='long'><p>Sets the interpolator of this transition. By default, the interpolator\n is null, which means that the Animator created by the transition\n will have its own specified interpolator. If the interpolator of a\n Transition is set, that interpolator will override the Animator interpolator.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>interpolator</span> : Object {TimeInterpolator}<div class='sub-desc'><p>The time interpolator used by the transition</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.\n@attr ref android.R.styleable#Transition_interpolator</p>\n</div></li></ul></div></div></div><div id='method-setMatchOrder' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setMatchOrder' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setMatchOrder' class='name expandable'>setMatchOrder</a>( <span class='pre'>matches</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the order in which Transition matches View start and end values. ...</div><div class='long'><p>Sets the order in which Transition matches View start and end values.\n <p>\n The default behavior is to match first by android.view.View.getTransitionName(),\n then by View instance, then by android.view.View.getId() and finally\n by its item ID if it is in a direct child of ListView. The caller can\n choose to have only some or all of the values of <a href=\"#!/api/android.transition.Transition-property-MATCH_INSTANCE\" rel=\"android.transition.Transition-property-MATCH_INSTANCE\" class=\"docClass\">MATCH_INSTANCE</a>,\n <a href=\"#!/api/android.transition.Transition-property-MATCH_NAME\" rel=\"android.transition.Transition-property-MATCH_NAME\" class=\"docClass\">MATCH_NAME</a>, <a href=\"#!/api/android.transition.Transition-property-MATCH_ITEM_ID\" rel=\"android.transition.Transition-property-MATCH_ITEM_ID\" class=\"docClass\">MATCH_ITEM_ID</a>, and <a href=\"#!/api/android.transition.Transition-property-MATCH_ID\" rel=\"android.transition.Transition-property-MATCH_ID\" class=\"docClass\">MATCH_ID</a>. Only\n the match algorithms supplied will be used to determine whether Views are the\n the same in both the start and end Scene. Views that do not match will be considered\n as entering or leaving the Scene.\n </p></p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>matches</span> : Object {int[]}<div class='sub-desc'><p>A list of zero or more of <a href=\"#!/api/android.transition.Transition-property-MATCH_INSTANCE\" rel=\"android.transition.Transition-property-MATCH_INSTANCE\" class=\"docClass\">MATCH_INSTANCE</a>,\n                <a href=\"#!/api/android.transition.Transition-property-MATCH_NAME\" rel=\"android.transition.Transition-property-MATCH_NAME\" class=\"docClass\">MATCH_NAME</a>, <a href=\"#!/api/android.transition.Transition-property-MATCH_ITEM_ID\" rel=\"android.transition.Transition-property-MATCH_ITEM_ID\" class=\"docClass\">MATCH_ITEM_ID</a>, and <a href=\"#!/api/android.transition.Transition-property-MATCH_ID\" rel=\"android.transition.Transition-property-MATCH_ID\" class=\"docClass\">MATCH_ID</a>.\n                If none are provided, then the default match order will be set.</p>\n</div></li></ul></div></div></div><div id='method-setPathMotion' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setPathMotion' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setPathMotion' class='name expandable'>setPathMotion</a>( <span class='pre'>pathMotion</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the algorithm used to calculate two-dimensional interpolation. ...</div><div class='long'><p>Sets the algorithm used to calculate two-dimensional interpolation.\n <p>\n     Transitions such as <a href=\"#!/api/android.transition.ChangeBounds\" rel=\"android.transition.ChangeBounds\" class=\"docClass\">android.transition.ChangeBounds</a> move Views, typically\n     in a straight path between the start and end positions. Applications that desire to\n     have these motions move in a curve can change how Views interpolate in two dimensions\n     by extending PathMotion and implementing\n     float, float, float).\n </p>\n <p>\n     When describing in XML, use a nested XML tag for the path motion. It can be one of\n     the built-in tags <code>arcMotion</code> or <code>patternPathMotion</code> or it can\n     be a custom PathMotion using <code>pathMotion</code> with the <code>class</code>\n     attributed with the fully-described class name. For example:</p></p>\n\n<pre> {@code\n <changeBounds>\n     <pathMotion class=\"my.app.transition.MyPathMotion\"/>\n </pathMotion></changeBounds>\n }\n </pre>\n\n\n<p> <p>or</p></p>\n\n<pre> {@code\n <changeBounds>\n   <arcMotion android:minimumHorizontalAngle=\"15\"\n     android:minimumVerticalAngle=\"0\" android:maximumAngle=\"90\"/>\n </arcMotion></changeBounds>\n }\n </pre>\n\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>pathMotion</span> : Object {PathMotion}<div class='sub-desc'><p>Algorithm object to use for determining how to interpolate in two\n                   dimensions. If null, a straight-path algorithm will be used.\n@see <a href=\"#!/api/android.transition.ArcMotion\" rel=\"android.transition.ArcMotion\" class=\"docClass\">android.transition.ArcMotion</a>\n@see PatternPathMotion\n@see <a href=\"#!/api/android.transition.PathMotion\" rel=\"android.transition.PathMotion\" class=\"docClass\">android.transition.PathMotion</a></p>\n</div></li></ul></div></div></div><div id='method-setPropagation' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setPropagation' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setPropagation' class='name expandable'>setPropagation</a>( <span class='pre'>transitionPropagation</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the method for determining Animator start delays. ...</div><div class='long'><p>Sets the method for determining Animator start delays.\n When a Transition affects several Views like <a href=\"#!/api/android.transition.Explode\" rel=\"android.transition.Explode\" class=\"docClass\">android.transition.Explode</a> or\n <a href=\"#!/api/android.transition.Slide\" rel=\"android.transition.Slide\" class=\"docClass\">android.transition.Slide</a>, there may be a desire to have a \"wave-front\" effect\n such that the Animator start delay depends on position of the View. The\n TransitionPropagation specifies how the start delays are calculated.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>transitionPropagation</span> : Object {TransitionPropagation}<div class='sub-desc'><p>The class used to determine the start delay of\n                              Animators created by this Transition. A null value\n                              indicates that no delay should be used.</p>\n</div></li></ul></div></div></div><div id='method-setStartDelay' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-setStartDelay' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-setStartDelay' class='name expandable'>setStartDelay</a>( <span class='pre'>startDelay</span> ) : Object {android.transition.Transition}<span class=\"signature\"></span></div><div class='description'><div class='short'>Sets the startDelay of this transition. ...</div><div class='long'><p>Sets the startDelay of this transition. By default, there is no delay\n (indicated by a negative number), which means that the Animator created by\n the transition will have its own specified startDelay. If the delay of a\n Transition is set, that delay will override the Animator delay.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>startDelay</span> : Number<div class='sub-desc'><p>The length of the delay, in milliseconds.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object {android.transition.Transition}</span><div class='sub-desc'><p>This transition object.\n@attr ref android.R.styleable#Transition_startDelay</p>\n</div></li></ul></div></div></div><div id='method-toString' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/android.transition.Transition' rel='android.transition.Transition' class='defined-in docClass'>android.transition.Transition</a><br/><a href='source/Transition.html#android-transition-Transition-method-toString' target='_blank' class='view-source'>view source</a></div><a href='#!/api/android.transition.Transition-method-toString' class='name expandable'>toString</a>( <span class='pre'></span> )<span class=\"signature\"></span></div><div class='description'><div class='short'> ...</div><div class='long'>\n</div></div></div></div></div></div></div>","meta":{}});